package com.fidilaundry.app.util.dialogimport android.app.Dialogimport android.content.DialogInterfaceimport android.os.Bundleimport com.esotericsoftware.minlog.Log.infoimport com.fidilaundry.app.Rimport com.google.android.material.bottomsheet.BottomSheetBehaviorimport com.google.android.material.bottomsheet.BottomSheetDialogimport com.google.android.material.bottomsheet.BottomSheetDialogFragmentopen class RoundedBottomSheetDialogFragment : BottomSheetDialogFragment() {    override fun getTheme(): Int = R.style.BottomSheetDialogTheme//    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog = BottomSheetDialog(requireContext(), theme)    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        val dialog = BottomSheetDialog(requireContext(), theme)        dialog.behavior.state = BottomSheetBehavior.STATE_EXPANDED        dialog.behavior.isDraggable = false        return dialog    }}